const fastify = require('fastify')()
const tap = require('tap')
const fastifySwaggerUI = require('./index')

tap.test('fastify.swagger should exist', test => {
  test.plan(2)

  fastify.register(fastifySwaggerUI, {})

  fastify.ready(err => {
    test.error(err)
    test.ok(fastify.swagger)

    fastify.close()
  })
})
